import React, { useState } from "react";
import ReactPlayer from "react-player";
import { Modal, Button } from 'react-bootstrap';
import { useNavigate } from "react-router-dom";


const About = () => {
  const [show, setShow] = useState(false);
  const [msj, setMsj] = useState("");

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const nav = useNavigate();

  const handleButton = (value) => {
    if(value === "A") {
      setMsj("Compartes la visión y valores de la empresa nos gustaría trabajar en conjunto!")
    }
    if(value === "B") {
      setMsj("Hay que participar de forma activa para que esto suceda!")
    }
    handleShow();
  }

  const handleModal = () => {
    handleClose();
    nav('/main#contact');
    return window.location.reload()
  }

  return (
    <div id="about">  
      <div className="servDiv">
        <div className="bg-sevices">
          <div className="container" id="service">
            {/* <br /> */}
            <div className="row headAbout">
              <h1 className="title-about-litio">LI 3, el mineral que puede llevar a la humanidad al siguiente nivel y potenciar la región.</h1>
                <div className="player-video">
                  <ReactPlayer
                    className=""
                    url="https://vimeo.com/713428314"
                    width="640px"
                    playing={false}
                    height="360px"
                    controls={true}
                    muted={false}
                    playsinline={true}
                  />
                </div>
              <div>
                <h4>A. Invertiría en desarrollar tecnología propia para poder extraer los recursos y producir las baterías, sin depender de terceros, teniendo como prioridad el medioambiente y los derechos indígenas.
                </h4>
                <br/>
                <h4>
                  B. Mantendría y regularía a las empresas extranjeras, exigiendo mayor participación en los beneficios obtenidos...
                </h4>
              </div>
              <div className="btn-options-video-litio">
                <button className="btn-a-b" onClick={() => handleButton("A")} >A</button>
                <button className="btn-a-b" onClick={() => handleButton("B")} >B</button>
              </div>
              <div className="col mt-3 ">
              </div>
            </div>

            <div
              className="row align-bottom mt-3 servicecol"
              style={{
                width: "80%",
                margin: "auto",
              }}
            >
              <div className="col">
                <div className="row">
  {/* contenido */}
                </div>
              </div>
            </div>
            {/** Carrousell on mobile */}
            <div
              id="carouselServicios"
              className="carousel slide "
              data-bs-ride="carousel"
            >
              <div className="carousel-inner">
  {/* CONTENIDO */}
              </div>
            </div>

            {/** FIN Carrousell on mobile */}
          </div>
        </div>

        {/* <div className="servicebottom mt-5">
          <div className="row align-bottom servicecol">
            <div className="col-md-4 col-sd-12"></div>

            <div className="col-md-4 col-sd-12"></div>
            <div className="col-md-4 col-sd-12"></div>
          </div>
        </div> */}
      </div>
      <div className="modal">

      <Modal
        style={{ marginTop: "35vh" }}
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            Perfecto !!
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {msj}
        </Modal.Body>
        <Modal.Footer>
          {/* <Button variant="secondary" onClick={handleClose}>
            No
          </Button> */}
          <Button variant="primary" onClick={() => handleModal()} >Continuar</Button>
        </Modal.Footer>
      </Modal>
        </div>
    </div>
  );
};

export default About;
