import React from "react";
import ReactPlayer from "react-player";
// import VideoPlayer from "../components/VideoPlayer";
import video from "../assets/video/intro.mp4";


import p1 from "../assets/img/person1.png";
import p2 from "../assets/img/person2.png";
import p3 from "../assets/img/person3.png";
import p4 from "../assets/img/person4.png";
import p5 from "../assets/img/person1.png";
import p6 from "../assets/img/person2.png";
import p7 from "../assets/img/person3.png";

const About = () => {
  const personas = [
    {
      url: p1,
      nombre: "integrante 1",
    },
    {
      url: p2,
      nombre: "integrante 2",
    },
    {
      url: p3,
      nombre: "integrante 3",
    },
    {
      url: p4,
      nombre: "integrante 4",
    },
    {
      url: p5,
      nombre: "integrante 5",
    },
    {
      url: p6,
      nombre: "integrante 6",
    },
    {
      url: p7,
      nombre: "integrante 7",
    },
  ];
  return (
    <div id="about">
      <div className="servDiv">
        <div className="bg-sevices">
          <div className="container" id="service">
            <br />
            <div className="row headservicios">
              <h1>LI 3 El mineral que puede llevar a la Humanidad al siguiente nivel y potenciar la región.</h1>
               <div className="player-video">
                <ReactPlayer
                  className=""
                  url={video}
                  width="640px"
                  playing={false}
                  height="360px"
                  controls={true}
                  muted={false}
                  playsinline={true}
                  // onEnded={handleEnd}
                />
              </div>
              <div>
                <h4>A. Invertiría en desarrollar tecnología propia para poder extraer los recursos y producir las baterías, sin depender de terceros, teniendo como prioridad el medioambiente y los derechos indígenas.
                </h4>
                <br/>
                <h4>
                  B. Mantendría y regularía a las empresas extranjeras, exigiendo mayor participación en los beneficios obtenidos...
                </h4>
              </div>
              <div className="btn-video-litio">
                <button>A</button>
                <button>B</button>
              </div>
              <div className="col mt-3 ">
              </div>
            </div>

            <div
              className="row align-bottom mt-3 servicecol"
              style={{
                width: "80%",
                margin: "auto",
              }}
            >
              <div className="col">
                <div className="row">
  {/* contenido */}
                </div>
              </div>
            </div>
            {/** Carrousell on mobile */}
            <div
              id="carouselServicios"
              className="carousel slide "
              data-bs-ride="carousel"
            >
              <div className="carousel-inner">
  {/* CONTENIDO */}
              </div>
            </div>

            {/** FIN Carrousell on mobile */}
          </div>
        </div>

        {/* <div className="servicebottom mt-5">
          <div className="row align-bottom servicecol">
            <div className="col-md-4 col-sd-12"></div>

            <div className="col-md-4 col-sd-12"></div>
            <div className="col-md-4 col-sd-12"></div>
          </div>
        </div> */}
      </div>
    </div>
  );
};

export default About;
